                    UPDATE `mail_template` SET `id` = 11,`slug` = 'welcome-bonus',`title` = '{name} - Welcome bonus',`content` = '<table width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td style=\"line-height: 150%; font-size: 19px; font-family: Roboto, Arial; color: #505050;\" align=\"left\">Congratulations! You have received a free ticket for {lottery_name} as a bonus!</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding-top: 30px;padding-bottom:30px;\" align=\"center\">{numbers}</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" align=\"center\" style=\"border-top:1px solid #E8E8E8;\">\r\n<tbody>\r\n<tr>\r\n<td style=\"line-height: 150%; font-size: 16px; font-family: Roboto, Arial; color: #727e8b;padding-top:2px;font-weight:bold;padding-top:30px;\" align=\"left\">What you can do now?</td>\r\n</tr>\r\n<tr>\r\n<td style=\"line-height: 150%; font-size: 16px; font-family: Roboto, Arial; color: #727e8b;padding-top:1px;\" align=\"left\">You can check the details of all your purchased tickets anytime. Simply click the button below.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding-top: 30px;padding-bottom:15px;\" align=\"center\">{button}</td>\r\n</tr>\r\n</tbody>\r\n</table>',`text_content` = 'Congratulations! You have received a free ticket for {lottery_name} as a bonus!\r\n\r\n{numbers}\r\n\r\nWhat you can do now?\r\nYou can check the details of all your purchased tickets anytime. Simply click the button below.\r\n\r\n{button}',`additional_translates` = 'a:4:{s:14:\"ticket_details\";a:2:{s:5:\"label\";s:19:\"Ticket details text\";s:11:\"translation\";s:14:\"Ticket details\";}s:9:\"draw_date\";a:2:{s:5:\"label\";s:14:\"Draw date text\";s:11:\"translation\";s:9:\"Draw date\";}s:13:\"purchase_date\";a:2:{s:5:\"label\";s:23:\"Bonus receive date text\";s:11:\"translation\";s:17:\"Bonus received on\";}s:6:\"button\";a:2:{s:5:\"label\";s:13:\"Action button\";s:11:\"translation\";s:15:\"view my tickets\";}}',`is_partial` = 1 WHERE `mail_template`.`id` = 11;
                    UPDATE `mail_template` SET `id` = 12,`slug` = 'draw-notification',`title` = '{name} - Check draw results!',`content` = '<table width=\"100%\" style=\"border-bottom:1px solid #E8E8E8;\">\r\n<tbody>\r\n<tr>\r\n<td style=\"line-height: 150%; font-size: 19px; font-family: Roboto, Arial; color: #505050; padding-bottom: 24px;\" align=\"left\">Check out the draw results to see if you have won a prize! If you haven\'t been able to pick all the numbers correctly, you still may be eligible for one of the lower tier prizes.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" align=\"center\" cellpadding=\"0\" cellspacing=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding-top: 28px;padding-bottom:25px;\" align=\"center\">{numbers}</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" align=\"center\" style=\"border-top:1px solid #E8E8E8;\">\r\n<tbody>\r\n<tr>\r\n<td style=\"line-height: 150%; font-size: 16px; font-family: Roboto, Arial; color: #727e8b;padding-top:23px;font-weight:bold;\" align=\"left\">What else can you do?</td>\r\n</tr>\r\n<tr>\r\n<td style=\"line-height: 150%; font-size: 16px; font-family: Roboto, Arial; color: #727e8b;padding-top:1px;\" align=\"left\">Make sure to check your numbers thoroughly. Click the button below to see if you have won a prize in the last draw.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding-top: 30px;\" align=\"center\">{button}</td>\r\n</tr>\r\n</tbody>\r\n</table>',`text_content` = 'Check out the draw results to see if you have won a prize! If you haven\'t been able to pick all the numbers correctly, you still may be eligible for one of the lower tier prizes.\r\n\r\n{numbers}\r\n\r\nWhat you can do now?\r\nYou can check if you won and the details of the ticket. Just click the button below.\r\n\r\n{button}',`additional_translates` = 'a:3:{s:6:\"button\";a:2:{s:5:\"label\";s:34:\"Check your winnings button content\";s:11:\"translation\";s:21:\"Check your winnings!\";}s:12:\"draw_results\";a:2:{s:5:\"label\";s:17:\"Draw results text\";s:11:\"translation\";s:41:\"Results of the draws you\'ve participated:\";}s:9:\"draw_date\";a:2:{s:5:\"label\";s:14:\"Draw date text\";s:11:\"translation\";s:9:\"Draw date\";}}',`is_partial` = 1 WHERE `mail_template`.`id` = 12;
                    INSERT INTO `mail_template` SET `id` = 13,`slug` = 'welcome-mail',`title` = '{name} - Welcome!',`content` = '<table width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td style=\"line-height: 150%; font-size: 19px; font-family: Roboto, Arial; color: #4c4e5c; padding-bottom: 28px;\" align=\"left\">Thank you for confirming your e-mail address. Your account is fully activated now, so you can enjoy all of the features.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\">\r\n<tr>\r\n<td style=\"line-height: 150%; font-size: 16px; font-family: Roboto, Arial; color: #727e8b;padding-top:4px;font-weight:bold;\" align=\"left\">What you can do now?</td>\r\n</tr>\r\n<tr>\r\n<td style=\"line-height: 150%; font-size: 16px; font-family: Roboto, Arial; color: #727e8b;padding-top:1px;\" align=\"left\">Check out the amazing jackpots waiting just for you! Pick your lucky numbers and play now!</td>\r\n</tr>\r\n</table>\r\n<table width=\"100%\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding-top: 25px;padding-bottom:25px;\" align=\"center\">{lotteries_widget}</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"line-height: 150%; font-size: 16px; font-family: Roboto, Arial; color: #727e8b;padding-top:2px;font-weight:bold;\" align=\"left\">Is that all?</td>\r\n</tr>\r\n<tr>\r\n<td style=\"line-height: 150%; font-size: 16px; font-family: Roboto, Arial; color: #727e8b;padding-top:1px;\" align=\"left\">No! The world of lotteries is huge and brings you new possibilities to win big every day. Click the button below to see all of the lotteries we offer.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding-top: 30px;padding-bottom:15px;\" align=\"center\">{button}</td>\r\n</tr>\r\n</tbody>\r\n</table>',`text_content` = 'Thank you for confirming your e-mail address. Your account is fully activated now, so you can enjoy all of the features.\r\n\r\nWhat you can do now?\r\nCheck out the amazing jackpots waiting just for you! Pick your lucky numbers and play now!\r\n\r\nIs that all?\r\nNo! The world of lotteries is huge and brings you new possibilities to win big every day. Click the button below to see all of the lotteries we offer.\r\n\r\n{button}',`additional_translates` = 'a:1:{s:6:\"button\";a:2:{s:5:\"label\";s:13:\"Action button\";s:11:\"translation\";s:8:\"play now\";}}',`is_partial` = 1")
                    ->execute();
